var modal=document.querySelector(".modal"),modalBg=document.querySelector(".modal-bg"),closeButton=document.querySelector(".close-button"),openImg=document.getElementsByClassName("open-img");closeButton.addEventListener("click",function(){modal.classList.toggle("closed"),modalBg.classList.toggle("closed")});for(var i=0;i<openImg.length;i++)openImg[i].addEventListener("click",function(){modal.classList.toggle("closed"),modalBg.classList.toggle("closed")});